import { z } from "zod";
import { validate } from "../middleware";

const criarComodoSchema = z.object({
  body: z.object({
    imovelId: z.string().nonempty({ message: "ID do imóvel é obrigatório." }),
    tipo: z.string().nonempty({ message: "O tipo do cômodo é obrigatório." }),
    numero: z
      .string()
      .nonempty({ message: "O número do cômodo é obrigatório." }),
  }),
});

const validarCriarComodo = validate(criarComodoSchema);

export { validarCriarComodo };
